package com.example.arthavendashboard.Activity

import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.LinearLayout
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.arthavendashboard.Adapter.PaintingListAdapter
import com.example.arthavendashboard.Model.Painting
import com.example.arthavendashboard.R

class MainActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        val paintings = listOf(
            Painting(1, "Starry Night", "Vincent van Gogh", "data:image/jpeg;base64,"),
            Painting(2, "Mona Lisa", "Leonardo da Vinci", "data:image/jpeg;base64,"),
            Painting(3, "The Persistence of Memory", "Salvador Dali", "https://example.com/persistence_of_memory.jpg"),
            // Add more paintings as needed
        )

        val recyclerView = findViewById<RecyclerView>(R.id.rv_paintings)
        recyclerView.layoutManager = LinearLayoutManager(this)
        recyclerView.adapter = PaintingListAdapter(paintings)



        val cartLayout = findViewById<LinearLayout>(R.id.cart_layout)
        cartLayout.setOnClickListener {
            val intent = Intent(this, CartActivity::class.java)
            startActivity(intent)
        }

        val wishlistLayout = findViewById<LinearLayout>(R.id.wishlist_layout)
        wishlistLayout.setOnClickListener {
            val intent = Intent(this, WishlistActivity::class.java)
            startActivity(intent)
        }

        val profileLayout = findViewById<LinearLayout>(R.id.profile_layout)
        profileLayout.setOnClickListener {
            val intent = Intent(this, ProfileActivity::class.java)
            startActivity(intent)
        }

    }
}